import React, { useEffect, useState } from 'react';
import axios from 'axios';
// import DatePicker from 'react-datepicker';
// import 'react-datepicker/dist/react-datepicker.css';

function App() {
  const [message, setMessage] = useState('');
  const [data, setData] = useState([]);
  const [myData, setMyData] = useState([]);
  const [selectedProducts, setSelectedProducts] = useState([]);
  const [selectedDate, setSelectedDate] = useState('');

  const handleDateChange = (event) => {
    setSelectedDate(event.target.value);
  };

  useEffect(() => {
    async function fetchData() {
      const response = await axios.get('http://localhost:3000/mydata');
      setMyData(response.data);
    }
    fetchData();
  }, []);

  function ProductTable() {
    const [products, setProducts] = useState([
      {
        id: 1,
        name: 'Product 1',
        date: new Date().toISOString().substr(0, 10),
      },
      {
        id: 2,
        name: 'Product 2',
        date: new Date().toISOString().substr(0, 10),
      },
      {
        id: 3,
        name: 'Product 3',
        date: new Date().toISOString().substr(0, 10),
      },
    ]);

    const handleDateChange = (event, productId) => {
      const updatedProducts = products.map((product) => {
        if (product.id === productId) {
          return { ...product, date: event.target.value };
        }
        return product;
      });
      setProducts(updatedProducts);
    };

    const handleCheckboxChange = (productId) => {
      const productIndex = selectedProducts.indexOf(productId);
      if (productIndex === -1) {
        // Product not selected, add to selected products
        setSelectedProducts([...selectedProducts, productId]);
      } else {
        // Product already selected, remove from selected products
        const newSelectedProducts = [
          ...selectedProducts.slice(0, productIndex),
          ...selectedProducts.slice(productIndex + 1),
        ];
        setSelectedProducts(newSelectedProducts);
      }
    };

    const handleAddToCartClick = () => {
      console.log('Selected Products:', selectedProducts);
      // Add code here to add selected products to cart
    };

    return (
      <div>
        <table>
          <thead>
            <tr>
              <th>ID</th>
              <th>Name</th>
              <th>Quantity</th>
              {/* <th>ShipOnWeekends</th>
            <th>MaxDaysToShip</th> */}
              <th>Select</th>
              <th>OrderDate</th>
            </tr>
          </thead>
          <tbody>
            {myData.map((item) => (
              <tr key={item.productId}>
                <td>{item.productId}</td>
                <td>{item.productName}</td>
                <td>{item.inventoryQuantity}</td>
                {/* <td>{item.shipOnWeekends.toString()}</td>
              <td>{item.maxBusinessDaysToShip}</td> */}

                <td>
                  <input
                    type="checkbox"
                    checked={selectedProducts.includes(item.productId)} //{item.selected}
                    onChange={() => handleCheckboxChange(item.productId)}
                  />
                </td>
                <td>
                  <input
                    type="date"
                    value={item.date}
                    onChange={(event) =>
                      handleDateChange(event, item.productId)
                    }
                  />
                </td>
              </tr>
            ))}
          </tbody>
        </table>

        <button onClick={handleAddToCartClick()}>Add to Cart</button>
      </div>
    );
  }
}
export default App;
