import React, { useEffect, useState } from 'react';
import axios from 'axios';

function App() {
  const [message, setMessage] = useState('');
  const [data, setData] = useState([]);
  const [myData, setMyData] = useState([]);
  const [selectedProducts, setSelectedProducts] = useState([]);
  const [selectedDate, setSelectedDate] = useState(
    new Date().toISOString().substr(0, 10)
  );

  useEffect(() => {
    async function fetchData() {
      const response = await axios.get('http://localhost:3000/mydata');
      setMyData(response.data);
    }
    fetchData();
  }, []);
  const [selectedProductId, setSelectedProductId] = useState(null);

  // const handleDateSelect = (event) => {
  //   setSelectedDate(event.target.value);
  // };

  const handleAddClick = (productId, maxBusinessDaysToShip) => {
    const productToUpdate = myData.find((item) => item.productId === productId);
    const dateToAdd = new Date(selectedDate);
    dateToAdd.setDate(dateToAdd.getDate() + maxBusinessDaysToShip);

    const updatedProducts = myData.map((item) => {
      if (item.productId === productId) {
        return {
          ...item,
          calculatedShippingDate: dateToAdd.toISOString().substr(0, 10),
        };
      } else {
        return item;
      }
    });

    setSelectedProducts([...selectedProducts, productId]);
    setMyData(updatedProducts);
  };

  const handleRemoveClick = (productId) => {
    const updatedProducts = selectedProducts.filter(
      (item) => item.productId !== productId
    );
    setSelectedProducts(updatedProducts);
  };

  const handleDateChange = (event, productId) => {
    const updatedProducts = selectedProducts.map((item) =>
      item.productId === productId
        ? { ...item, selectedDate: event.target.value }
        : item
    );
    setSelectedProducts(updatedProducts);
  };

  return (
    <div>
      <table>
        <thead>
          <tr>
            <th>ID</th>
            <th>Name</th>
            <th>Quantity</th>
            {/* <th>ShipOnWeekends</th> */}
            <th>MaxDaysToShip</th>

            <th>OrderDate</th>
            <th>Add To Cart</th>
            <th>Calculated Shipping Date</th>
            {/* <th>ShppingDate</th> */}
          </tr>
        </thead>
        <tbody>
          {myData.map((item) => {
            const selectedProduct = selectedProducts.find(
              (product) => product.productId === item.productId
            );
            const calculatedDate =
              selectedProduct && selectedProduct.selectedDate
                ? new Date(selectedProduct.selectedDate)
                : null;
            if (calculatedDate) {
              calculatedDate.setDate(calculatedDate.getDate() + item.value);
            }
            const calculatedDateString = calculatedDate
              ? calculatedDate.toISOString().substr(0, 10)
              : null;
            return (
              <tr key={item.productId}>
                <td>{item.productId}</td>
                <td>{item.productName}</td>
                <td>{item.inventoryQuantity}</td>
                <td>
                  <input
                    type="date"
                    value={
                      selectedProduct
                        ? selectedProduct.selectedDate
                        : new Date().toISOString().substr(0, 10)
                    }
                    min={new Date().toISOString().substr(0, 10)}
                    onChange={(event) =>
                      handleDateChange(event, item.productId)
                    }
                  />
                </td>
                <td>{calculatedDateString}</td>
                <td>
                  {selectedProduct ? (
                    <button onClick={() => handleRemoveClick(item.productId)}>
                      Remove
                    </button>
                  ) : (
                    <button onClick={() => handleAddClick(item.productId)}>
                      Add
                    </button>
                  )}
                </td>
              </tr>
            );
          })}
        </tbody>
      </table>
    </div>
  );
}

export default App;
