import React, { useEffect, useState } from 'react';
import axios from 'axios';

function App() {
  const [message, setMessage] = useState('');
  const [data, setData] = useState([]);
  const [myData, setMyData] = useState([]);

  useEffect(() => {
    async function fetchData() {
      const response = await axios.get('http://localhost:3000/mydata');
      setMyData(response.data);
    }
    fetchData();
  }, []);

  const handleCheckboxChange = (userId) => {
    // handle checkbox change for a specific user
  };

  return (
    <table>
      <thead>
        <tr>
          <th>ID</th>
          <th>Name</th>
          <th>Quantity</th>
          <th>ShipOnWeekends</th>
          <th>MaxDaysToShip</th>
          <th>Select</th>
        </tr>
      </thead>
      <tbody>
        {myData.map((item) => (
          <tr key={item._id}>
            <td>{item.productId}</td>
            <td>{item.productName}</td>
            <td>{item.inventoryQuantity}</td>
            <td>{item.shipOnWeekends.toString()}</td>
            <td>{item.maxBusinessDaysToShip}</td>
            <td>
              <input
                type="checkbox"
                checked={item.selected}
                onChange={() => handleCheckboxChange(item._id)}
              />
            </td>
          </tr>
        ))}
      </tbody>
    </table>
  );
}

