import React, { useEffect, useState } from 'react';
import axios from 'axios';
// import DatePicker from 'react-datepicker';
// import 'react-datepicker/dist/react-datepicker.css';

function App() {
  const [message, setMessage] = useState('');
  const [data, setData] = useState([]);
  const [myData, setMyData] = useState([]);
  const [selectedProducts, setSelectedProducts] = useState([]);
  const [selectedDate, setSelectedDate] = useState(
    new Date().toISOString().substr(0, 10)
  );

  useEffect(() => {
    async function fetchData() {
      const response = await axios.get('http://localhost:3000/mydata');
      setMyData(response.data);
    }
    fetchData();
  }, []);
  const [selectedProductId, setSelectedProductId] = useState(null);

  const handleDateSelect = (event) => {
    setSelectedDate(event.target.value);
  };

  const handleAddClick = () => {
    console.log('Selected Products:', selectedProducts);
    // Add code here to add selected products to cart
  };

  return (
    <div>
      <table>
        <thead>
          <tr>
            <th>ID</th>
            <th>Name</th>
            <th>Quantity</th>
            {/* <th>ShipOnWeekends</th>
            <th>MaxDaysToShip</th> */}

            <th>OrderDate</th>
            {/* <th>ShppingDate</th> */}
          </tr>
        </thead>
        <tbody>
          {myData.map((item) => (
            <tr key={item.productId}>
              <td>{item.productId}</td>
              <td>{item.productName}</td>
              <td>{item.inventoryQuantity}</td>
              {/* <td>{item.shipOnWeekends.toString()}</td>
              <td>{item.maxBusinessDaysToShip}</td> */}

              <td>
                <input
                  type="date"
                  value={selectedDate}
                  min={new Date().toISOString().substr(0, 10)}
                  onChange={handleDateSelect}
                  // disabled={selectedProductId !== item.productId}
                />
              </td>
              {/* <button onClick={handleAddToCartClick()}>Add</button> */}
              <button onClick={() => handleAddClick(item.productId)}>
                Add
              </button>
              <td>Calculated Shipping date</td>
              {/* <td>{calculatedDateString}</td> */}
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default App;
