import React, { useEffect, useState } from 'react';
import axios from 'axios';

function App() {
  const [message, setMessage] = useState('');
  const [data, setData] = useState([]);
  const [myData, setMyData] = useState([]);
  const [selectedProducts, setSelectedProducts] = useState([]);
  const [selectedDate, setSelectedDate] = useState(
    new Date().toISOString().substr(0, 10)
  );

  useEffect(() => {
    async function fetchData() {
      const response = await axios.get('http://localhost:3000/mydata');
      setMyData(response.data);
    }
    fetchData();
  }, []);
  const [selectedProductId, setSelectedProductId] = useState(null);

  const handleDateSelect = (event) => {
    setSelectedDate(event.target.value);
  };

  // const handleAddClick = () => {
  //   console.log('Selected Products:', selectedProducts);
  //   // Add code here to add selected products to cart
  // };
  const handleAddClick = (productId) => {
    console.log('Selected Products:', selectedProducts);
    const selectedProduct = myData.find((item) => item.productId === productId);
    const selectedProductIndex = selectedProducts.findIndex(
      (item) => item.productId === productId
    );

    if (selectedProductIndex >= 0) {
      setSelectedProducts([
        ...selectedProducts.slice(0, selectedProductIndex),
        {
          productId,
          orderDate: selectedDate,
          shippingDate: new Date(selectedDate)
            .setDate(new Date(selectedDate).getDate() + 10)
            .toISOString()
            .substr(0, 10),
        },
        ...selectedProducts.slice(selectedProductIndex + 1),
      ]);
    } else {
      setSelectedProducts([
        ...selectedProducts,
        {
          productId,
          orderDate: selectedDate,
          shippingDate: new Date(selectedDate)
            .setDate(new Date(selectedDate).getDate() + 10)
            .toISOString()
            .substr(0, 10),
        },
      ]);
    }
  };

  return (
    <div>
      <table>
        <thead>
          <tr>
            <th>ID</th>
            <th>Name</th>
            <th>Quantity</th>
            {/* <th>ShipOnWeekends</th>
            <th>MaxDaysToShip</th> */}

            <th>OrderDate</th>
            <th>Add To Cart</th>
            <th>Calculated Shipping Date</th>
            {/* <th>ShppingDate</th> */}
          </tr>
        </thead>
        <tbody>
          {myData.map((item) => (
            <tr key={item.productId}>
              <td>{item.productId}</td>
              <td>{item.productName}</td>
              <td>{item.inventoryQuantity}</td>
              {/* <td>{item.shipOnWeekends.toString()}</td>
              <td>{item.maxBusinessDaysToShip}</td> */}

              <td>
                <input
                  type="date"
                  value={selectedDate}
                  min={new Date().toISOString().substr(0, 10)}
                  onChange={handleDateSelect}
                  // disabled={selectedProductId !== item.productId}
                />
              </td>
              <td>
                {/* <button onClick={handleAddToCartClick()}>Add</button> */}
                <button onClick={() => handleAddClick(item.productId)}>
                  Add
                </button>
              </td>
              <td>
                {
                  selectedProducts.find(
                    (product) => product.productId === item.productId
                  )?.shippingDate
                }
              </td>
              {/* <td>Calculated Shipping date</td> */}
              {/* <td>{calculatedDateString}</td> */}
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default App;
